import React, { useState } from 'react';
import './App.css';
import BobaRanking from './BobaRanking';
import BobaForm from './BobaForm';

function App() {
  const [bobaPlaces, setBobaPlaces] = useState([
    { name: "Boba Guys", location: "San Francisco", rating: 9, type: "milk tea", rank: 1 },
    { name: "Sharetea", location: "Los Angeles", rating: 8, type: "fruit tea", rank: 2 },
    { name: "Gong Cha", location: "New York City", rating: 7, type: "milk tea", rank: 3 },
    { name: "T4", location: "San Jose", rating: 9, type: "fruit tea", rank: 4 },
    { name: "CoCo Fresh Tea", location: "Seattle", rating: 6, type: "milk tea", rank: 5 },
  ]);

  const [showForm, setShowForm] = useState(false);
  const [password, setPassword] = useState("");
  const [views, setViews] = useState(0);

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleFormSubmit = (newBobaPlace) => {
    const updatedBobaPlaces = [...bobaPlaces, newBobaPlace];
    setBobaPlaces(updatedBobaPlaces);
    setShowForm(false);
  };

  const handleViewIncrement = () => {
    setViews(views + 1);
  };

  const handleRankChange = (newRank, oldRank) => {
    const updatedBobaPlaces = bobaPlaces.map((bobaPlace) => {
      if (bobaPlace.rank === oldRank) {
        return { ...bobaPlace, rank: newRank };
      } else if (bobaPlace.rank > oldRank && bobaPlace.rank <= newRank) {
        return { ...bobaPlace, rank: bobaPlace.rank - 1 };
      } else if (bobaPlace.rank < oldRank && bobaPlace.rank >= newRank) {
        return { ...bobaPlace, rank: bobaPlace.rank + 1 };
      } else {
        return bobaPlace;
      }
    });
    setBobaPlaces(updatedBobaPlaces);
  };

  const handleFormDisplay = () => {
    setShowForm(!showForm);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Boba Rankings</h1>
        <p>Views: {views}</p>
      </header>
      <div className="App-body">
        <BobaRanking bobaPlaces={bobaPlaces} handleRankChange={handleRankChange} />
        <button onClick={handleFormDisplay}>Add a Boba Place</button>
        {showForm ? <BobaForm handleFormSubmit={handleFormSubmit} /> : null}
        <input type="password" value={password} onChange={handlePasswordChange} />
        {password === "secret" ? <p>You have access to add new boba places</p> : null}
      </div>
      <div className="App-footer">
        <p>Â© 2023 Boba Rankings Inc.</p>
      </div>
    </div>
  );
}

export default App;
