import React, { Component } from 'react';
import './Counter.css';

class Counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: this.props.startCounter,
      displayCounter: this.props.startCounter,
    };
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      const newCounter = this.state.counter + 1;
      this.setState({ counter: newCounter, displayCounter: newCounter });
    }, 3000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.displayCounter !== this.state.displayCounter) {
      this.animateCounter();
    }
  }

  animateCounter = () => {
    this.counterRef.classList.add('AnimatedCounter--flap');
    setTimeout(() => {
      this.counterRef.classList.remove('AnimatedCounter--flap');
    }, 500);
  };

  render() {
    return (
      <div className="AnimatedCounter" ref={(el) => (this.counterRef = el)}>
        <span>{this.state.displayCounter}</span>
      </div>
    );
  }
}

export default AnimatedCounter;
